package com.upskill.java_1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Hello world, This is Rony");
		
		/*		There are two Datatypes in java variables:
		1.    primitive: Pre-Define by Java (int, short, long, float, double)
		2.    non-primitive : Not Defined (Class, Array, Object)	
*/
		int a = 35;
		int b = 55;
		int c = a + b;
		System.out.println(c);
		
		String city = "New York";
		String state = "NY";
		
		System.out.println(city);
		System.out.println(state);
		
		boolean isEasy = true;
		System.out.println(isEasy);
		
		
		
	}

}
